// Import necessary dependencies
import React, { useState, useEffect } from "react";
import { Box, Button } from "@chakra-ui/react"; // Adjust imports as needed
import Layout from "./Layout"; // Make sure to import your Layout component

function FetchQuote() {
  const [quote, setQuote] = useState('');

  useEffect(() => {
    const url = `https://jsonplaceholder.typicode.com/todos/${todoG}`;
    getQuote(url);
  }, [todoG]);

  function getQuote(url) {
    console.log('Fetching quote...');
    fetch(url)
      .then(response => response.json())
      .then(data => {
        try {
          setQuote(data.title);
        } catch {
          setQuote('Error getting quote');
        }
      });
  }

  return (
    <p>Quote {todoG}: {quote}</p>
  );
}

function handleRandomQuote() {
  const randomTodoG = Math.floor(Math.random() * 200) + 1;
  console.log(randomTodoG);
  return randomTodoG;
}

function QuoteCheck() {
  const [showQuote, setShowQuote] = useState(false);
  const [todoG, setTodoG] = useState(1); // Initialize todoG with 1

  const handleButtonClick = (show) => {
    setShowQuote(show);
  };

  const handleRandomizeQuote = () => {
    const randomTodo = handleRandomQuote();
    setTodoG(randomTodo);
  };

  return (
    <Layout>
      <Box p="5" borderWidth="1px" borderRadius="md" boxShadow="md">
        <button onClick={() => handleButtonClick(true)}>Get Quote</button>
        <br />
        <button onClick={() => handleButtonClick(false)}>Hide Quote</button>
        <br />
        {showQuote && <FetchQuote />}
        <button onClick={handleRandomizeQuote}>Randomize Quote</button>
        <br />
        {/* Add other components or UI elements as needed */}
      </Box>
    </Layout>
  );
}

export default QuoteCheck;
