//import * as React from "react";
import React, { useState, useEffect } from "react";
import { Box, Button, Heading } from "@chakra-ui/react";
import Layout from "./Layout";
import { getQueriesForElement } from "@testing-library/react";
var todoG = 1;
function FetchQuote() {
    const [quote,setQuote] = useState('');
    const [loading,setLoading] = useState(true);

    useEffect(() => {
        let url = 'https://jsonplaceholder.typicode.com/todos/';
        url = url + todoG;
        getQuote(url);
      },[todoG]);

    function getQuote(url) {
        console.log('get');
        fetch(url)
        .then(response => response.json())
        .then(data => {
            try {
                setQuote(data.title)
            } catch {
                setQuote('Error getting quote')
            }
        })
    }
    return(
        <>
        <p>Quote {todoG}: {quote}</p>
        </>
    )
}
function handleRandomQuote() {
    todoG = (Math.floor(Math.random() * 200) + 1);
    console.log(todoG)
    return;
}

function QuoteCheck() {
    const [showQuote, setShowQuote] = useState(false);

    const handleButtonClick = (show) => {
        setShowQuote(show);
    };

    return (
        <Layout>
            <Box p="5" borderWidth="1px" borderRadius="md" boxShadow="md">
                <button onClick={() => handleButtonClick(true)}>Get Quote</button>
                <br/>
                <button onClick={() => handleButtonClick(false)}>HideQuote</button>
                <br/>
                {showQuote && <FetchQuote />}
                <button onClick={() =>  handleRandomQuote()}>randomize Quote</button>
                <br/>
                <Button colorScheme="blue" as="a" href="/" size="sm">
                    Go back
                </Button>
            </Box>

        </Layout>
    );
}

export default QuoteCheck;
