//import * as React from "react";
import React, { useState, useEffect } from "react";
import { Box, Button, Heading } from "@chakra-ui/react";
import Layout from "./Layout";
import { getQueriesForElement } from "@testing-library/react";

function FetchQuote() {
    const [quote,setQuote] = useState('');
    const [loading,setLoading] = useState(true);
    const [todo, setTodo] = useState(1);

    useEffect(()=>{
        getQuote();
        const intervalID = setInterval(()=>{
            getQuote()
        }, 24*60*60*1000);
        handleRandomQuote();
    return ()=>{
        clearInterval(intervalID);
    }
    },[])
    function getQuote(){
        let url = 'https://jsonplaceholder.typicode.com/todos/';
        url = url + todo;
        fetch(url)
        .then(response => response.json())
        .then(data => {
            try {
                setQuote(data.title)
            } catch {
                setQuote('Error getting quote')
            }
        })
    }
    function handleRandomQuote() {
        setTodo(Math.floor(Math.random() * 200) + 1);
        return;
    }
    return(
        <>
        <p>Quote {todo}: {quote}</p>

        </>
    )
}

function QuoteCheck() {
    const [showQuote, setShowQuote] = useState(false);

    const handleButtonClick = (show) => {
        setShowQuote(show);
    };

    return (
        <Layout>
            <Box p="5" borderWidth="1px" borderRadius="md" boxShadow="md">
                <button onClick={() => handleButtonClick(true)}>Get Quote</button>
                <br/>
                <button onClick={() => handleButtonClick(false)}>HideQuote</button>
                <br/>
                {showQuote && <FetchQuote />}
                <button onClick={() => FetchQuote.handleRandomQuote()}>randomize Quote</button>
                <br/>
                <Button colorScheme="blue" as="a" href="/" size="sm">
                    Go back
                </Button>
            </Box>

        </Layout>
    );
}

export default QuoteCheck;
