// Import statements remain the same

const todoG = 1; // Initial value for todo

function FetchQuote() {
  const [quote, setQuote] = useState("");
  const [loading, setLoading] = useState(true);
  const [todo, setTodo] = useState(todoG);

  useEffect(() => {
    getQuote();
    const intervalID = setInterval(() => {
      getQuote();
    }, 24 * 60 * 60 * 1000); // Update quote every 24 hours
    return () => clearInterval(intervalID);
  }, []); // Empty dependency array, runs effect only once

  function getQuote() {
    let url = "https://jsonplaceholder.typicode.com/todos/";
    url += todo;
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        try {
          setQuote(data.title);
        } catch {
          setQuote("Error getting quote");
        }
      });
  }

  return (
    <>
      <p>Quote {todo}: {quote}</p>
    </>
  );
}

function handleRandomQuote() {
  // Generate a random number between 1 and 200 (inclusive)
  const newTodo = Math.floor(Math.random() * 200) + 1;
  // Update the state of the FetchQuote component through its hook
  setTodo(newTodo); // This triggers a re-render of FetchQuote with the new todo value
}

function QuoteCheck() {
  const [showQuote, setShowQuote] = useState(false);

  const handleButtonClick = (show) => {
    setShowQuote(show);
  };

  return (
    <Layout>
      <Box p="5" borderWidth="1px" borderRadius="md" boxShadow="md">
        <button onClick={() => handleButtonClick(true)}>Get Quote</button>
        <br />
        <button onClick={() => handleButtonClick(false)}>Hide Quote</button>
        <br />
        {showQuote && <FetchQuote />}
        <button onClick={handleRandomQuote}>Randomize Quote</button>
        <br />
        <Button colorScheme="blue" as="a" href="/" size="sm">
          Go back
        </Button>
      </Box>
    </Layout>
  );
}

export default QuoteCheck;
