//import * as React from "react";
import React, { useState, useEffect } from "react";
import { Box, Button, Heading } from "@chakra-ui/react";
import Layout from "./Layout";
import { getQueriesForElement } from "@testing-library/react";

function FetchQuote() {
    const [quote,setQuote] = useState('');
    const [loading,setLoading] = useState(true);const [showQuote, setShowQuote] = useState(false);

    const handleButtonClick = () => {
        setShowQuote(true);
    };

    useEffect(()=>{
        getQuote();
        const intervalID = setInterval(()=>{
            getQuote()
        }, 24*60*60*1000);
    return ()=>{
        clearInterval(intervalID);
    }
    },[])
    function getQuote(){
        fetch('https://jsonplaceholder.typicode.com/todos/1')
        .then(response => response.json())
        .then(data => {
            try {
                setQuote(data.title)
            } catch {
                setQuote('Error getting quote')
            }
        })
    }
    return(
        <>
        <p>Hello world, look at console.</p>
        <h1>{quote}</h1>
        </>
    )
}
function QuoteCheck() {
    return (
        <Layout>
            <Box p="5" borderWidth="1px" borderRadius="md" boxShadow="md">
                <Heading as="h1" size="lg" mb="4">
                    Quote Check 1
                </Heading>
                <button onClick={handleButtonClick}>Get Quote</button>
                {showQuote && <FetchQuote />}
                <Button colorScheme="blue" as="a" href="/" size="sm">
                    Go back
                </Button>
            </Box>

        </Layout>
    );
}

export default QuoteCheck;
